// Resolution
const STEP_SIZE = 10;

// Intensity
const BASE_INTENSITY_OFFSET = 0.2;
const MAX_INTENSITY = 0.85;
const TURBULENCE_BASE = 0.2;
const TURBULENCE_TOP_MULT = 0.3;

// Vertical zones
const TOP_ZONE_RATIO = 0.3;

// Animation speed
const BASE_TIME_SPEED_X = 3.0;
const BASE_TIME_SPEED_Y = 1.5;
const TOP_TIME_SPEED_X = 3.0;
const TOP_TIME_SPEED_Y = 4.0;

// Turbulence frequency
const BASE_FREQ_X = 0.05;
const BASE_FREQ_Y = 0.05;
const TOP_FREQ_X = 0.2;
const TOP_FREQ_Y = 0.3;

// Turbulence strength
const TOP_EXTRA_SIN = 0.5;
const TOP_EXTRA_COS = 0.3;

// Color tuning
const RED_MAX = 255;
const GREEN_LOW = 100;
const GREEN_HIGH = 100;

// =========================
// 🔥 FIRE RENDER
// =========================

ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += STEP_SIZE) {
    for (let y = height; y > 0; y -= STEP_SIZE) {

        // Base vertical intensity
        let intensity =
            (height - y) / height +
            BASE_INTENSITY_OFFSET;

        let topFactor = 1 - (y / height);

        // Base turbulence
        let turbulence =
            Math.sin(x * BASE_FREQ_X + time * BASE_TIME_SPEED_X) *
            Math.cos(y * BASE_FREQ_Y + time * BASE_TIME_SPEED_Y);

        // Extra animation near top
        if (y < height * TOP_ZONE_RATIO) {
            turbulence +=
                Math.sin(x * TOP_FREQ_X + time * TOP_TIME_SPEED_X) *
                TOP_EXTRA_SIN;

            turbulence +=
                Math.cos(y * TOP_FREQ_Y + time * TOP_TIME_SPEED_Y) *
                TOP_EXTRA_COS;
        }

        intensity += turbulence * (TURBULENCE_BASE + topFactor * TURBULENCE_TOP_MULT);
        intensity = Math.max(0, Math.min(MAX_INTENSITY, intensity));

        // Fire color ramp
        let r, g, b = 0;

        if (intensity < 0.3) {
            r = RED_MAX * (intensity / 0.3);
            g = 0;
        } else if (intensity < 0.6) {
            r = RED_MAX;
            g = GREEN_LOW * ((intensity - 0.4) / 0.3);
        } else {
            r = RED_MAX;
            g = GREEN_LOW + (GREEN_HIGH - GREEN_LOW) * ((intensity - 0.2) / 0.55);
        }

        ctx.fillStyle(
            r * intensity,
            g * intensity,
            b
        );

        ctx.fillRect(x, y, STEP_SIZE, STEP_SIZE);
    }
}
