// =========================
// CONFIG
// =========================
const STEP = 4;
const ROT_SPEED = 0.5;
const SCALE = 0.02;

// =========================
// RENDER
ctx.clear(0, 0, 0);

let cx = width / 2;
let cy = height / 2;

for (let x = 0; x < width; x += STEP) {
    for (let y = 0; y < height; y += STEP) {

        let dx = x - cx;
        let dy = y - cy;
        let angle = Math.atan2(dy, dx) + time * ROT_SPEED;
        let dist = Math.sqrt(dx*dx + dy*dy);

        let hue = ((Math.sin(dist * SCALE - angle) + 1)/2) % 1;
        let rgb = ctx.hslToRgb(hue, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);
        ctx.fillRect(x, y, STEP, STEP);
    }
}
