// =========================
// CONFIG
// =========================
const TRI_SIZE = 30;
const ROT_SPEED = 0.5;

// =========================
// RENDER
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += TRI_SIZE) {
    for (let y = 0; y < height; y += TRI_SIZE) {

        let angle = time * ROT_SPEED + (x + y) * 0.01;
        let hue = (Math.sin(angle) * 0.5 + 0.5) % 1;
        let rgb = ctx.hslToRgb(hue, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);

        ctx.save();
        ctx.translate(x + TRI_SIZE/2, y + TRI_SIZE/2);
        ctx.rotate(angle);
        ctx.beginPath();
        ctx.moveTo(0, -TRI_SIZE/2);
        ctx.lineTo(TRI_SIZE/2, TRI_SIZE/2);
        ctx.lineTo(-TRI_SIZE/2, TRI_SIZE/2);
        ctx.closePath();
        ctx.fill();
        ctx.restore();
    }
}
