// =========================
// CONFIG
// =========================
const CELL = 20;
const WAVE_SPEED = 2.0;
const WAVE_FREQ = 0.15;

// =========================
// RENDER
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += CELL) {
    for (let y = 0; y < height; y += CELL) {

        let wave = Math.sin((x+y) * WAVE_FREQ + time * WAVE_SPEED);
        let intensity = (wave + 1)/2;

        let hue = ((x+y)/ (width+height) + time*0.05) % 1;
        let rgb = ctx.hslToRgb(hue, 1, 0.5);

        ctx.fillStyle(rgb.r*intensity, rgb.g*intensity, rgb.b*intensity);

        ctx.save();
        ctx.translate(x + CELL/2, y + CELL/2);
        ctx.rotate(Math.PI/4);
        ctx.fillRect(-CELL/2, -CELL/2, CELL, CELL);
        ctx.restore();
    }
}
