const CELL = 20;
const PULSE_SPEED = 2.0;

// =========================
// RENDER
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += CELL) {
    for (let y = 0; y < height; y += CELL) {

        let row = Math.floor(y / CELL);
        let col = Math.floor(x / CELL);

        let pulse = Math.sin(time * PULSE_SPEED + row + col);
        pulse = (pulse + 1)/2;

        ctx.fillStyle(255 * pulse, 100 * pulse, 200 * (1 - pulse));
        ctx.fillRect(x, y, CELL, CELL);
    }
}
