// =========================
// CONFIG
// =========================
const HEX_SIZE = 30;
const PULSE_SPEED = 2.0;

// =========================
ctx.clear(0, 0, 0);

let h = HEX_SIZE * Math.sqrt(3)/2;

for (let row = 0; row < Math.ceil(height/h); row++) {
    for (let col = 0; col < Math.ceil(width/HEX_SIZE); col++) {

        let x = col * HEX_SIZE + (row%2)*HEX_SIZE/2;
        let y = row * h;

        let pulse = (Math.sin(time*PULSE_SPEED + row + col) + 1)/2;
        let rgb = ctx.hslToRgb(pulse, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);

        ctx.beginPath();
        for (let i=0; i<6; i++) {
            let angle = Math.PI/3*i;
            let vx = x + HEX_SIZE/2 * Math.cos(angle);
            let vy = y + HEX_SIZE/2 * Math.sin(angle);
            if(i===0) ctx.moveTo(vx, vy);
            else ctx.lineTo(vx, vy);
        }
        ctx.closePath();
        ctx.fill();
    }
}
