// =========================
// CONFIG
// =========================
const STEP = 6;
const SPEED = 1.2;
const SCALE_X = 0.05;
const SCALE_Y = 0.05;
const COLOR_SHIFT = 0.6;

// =========================
// RENDER
// =========================
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += STEP) {
    for (let y = 0; y < height; y += STEP) {

        let v =
            Math.sin(x * SCALE_X + time * SPEED) +
            Math.sin(y * SCALE_Y + time * SPEED) +
            Math.sin((x + y) * 0.03 + time * SPEED);

        let h = (v * 0.15 + COLOR_SHIFT) % 1;
        let rgb = ctx.hslToRgb(h, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);
        ctx.fillRect(x, y, STEP, STEP);
    }
}
