// =========================
// CONFIG
// =========================
const GRID_SIZE = 12;
const MAX_RADIUS = 20;
const PULSE_SPEED = 2.0;

// =========================
// RENDER
ctx.clear(0, 0, 0);

for (let i = 0; i <= GRID_SIZE; i++) {
    for (let j = 0; j <= GRID_SIZE; j++) {

        let x = i * width / GRID_SIZE;
        let y = j * height / GRID_SIZE;

        let pulse = (Math.sin(time * PULSE_SPEED + i + j) + 1)/2;
        let radius = MAX_RADIUS * pulse;

        let hue = ((i+j)/GRID_SIZE + time*0.1) % 1;
        let rgb = ctx.hslToRgb(hue, 1, 0.5);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);
        ctx.fillCircle(x, y, radius);
    }
}
