// =========================
// CONFIG
// =========================
const LINE_HEIGHT = 4;
const SCAN_SPEED = 70;

// =========================
// RENDER
// =========================
ctx.clear(0, 0, 0);

for (let y = 0; y < height; y += LINE_HEIGHT) {

    let glow = Math.sin((y + time * SCAN_SPEED) * 0.05) * 0.5 + 0.5;

    ctx.fillStyle(
        0,
        255 * glow,
        120 * glow
    );

    ctx.fillRect(0, y, width, LINE_HEIGHT - 1);
}
