// =========================
// CONFIG
// =========================
const STEP = 4;           // resolution
const SPEED = 0.3;        // animation speed
const HUE_OFFSET = 0.0;   // base hue offset
const SATURATION = 1;     // 0-1
const LIGHTNESS = 0.5;    // 0-1

// =========================
// RENDER
// =========================
ctx.clear(0, 0, 0);

for (let x = 0; x < width; x += STEP) {
    for (let y = 0; y < height; y += STEP) {

        let hue = (Math.sin(x * 0.02 + time * SPEED) + Math.cos(y * 0.02 + time * SPEED)) * 0.5 + HUE_OFFSET;
        hue = (hue % 1 + 1) % 1; // wrap 0-1

        let rgb = ctx.hslToRgb(hue, SATURATION, LIGHTNESS);

        ctx.fillStyle(rgb.r, rgb.g, rgb.b);
        ctx.fillRect(x, y, STEP, STEP);
    }
}
