// =========================
// CONFIG
// =========================
const STEP = 6;
const SPIRALS = 4;
const SPEED = 0.8;

// =========================
ctx.clear(0,0,0);

let cx=width/2, cy=height/2;

for(let x=0;x<width;x+=STEP){
    for(let y=0;y<height;y+=STEP){

        let dx=x-cx, dy=y-cy;
        let dist=Math.sqrt(dx*dx+dy*dy);
        let angle=Math.atan2(dy,dx) + time*SPEED;
        let value=(Math.sin(dist*0.05*SPIRALS - angle*SPIRALS)+1)/2;

        ctx.fillStyle(255*value, 100*value, 255*(1-value));
        ctx.fillRect(x,y,STEP,STEP);
    }
}
